int position = 0;
int first_basement = 0;

void day_one() {
	for (int i = 0; i <= strlen(puzzle_input)-1; i++) {
		char c = puzzle_input[i];
		
		if (c == '(') {
			position += 1;
		} else if (c == ')') {
			position -= 1;
		}

		if (first_basement == 0 && position == -1) {
			first_basement = i + 1;
		}
	}

	printf("final position: %d\n", position);
	printf("first time in basement: %d\n", first_basement);
}