int position = 0;
int first_basement = 0;

void day_one() {
	for (int i = 0; i <= puzzle_input.length()-1; i++) {
		char c = puzzle_input[i];

		switch (c) {
			case '(': position += 1; break;
			case ')': position -= 1; break;
		}

		if (first_basement == 0 && position == -1) {
			first_basement = i + 1;
		}
	}

	std::cout << "final position: " << position << "\n";
	std::cout << "first time in basement: " << first_basement << "\n";
}